    var slidingLine = function slidingLine() {

        var nav = $('.tabs__list'),
            line = $('<div>').appendTo(nav),
            activeLi,
            lineWidth,
            liPos;

        function refresh() {
            activeLi = nav.find('li.active__item');

            lineWidth = activeLi.outerWidth();

            liPos = activeLi.position().left;

            // console.log(liPos);
        }

        refresh();

        nav.css('position', 'relative');

        function lineSet() {
            line.css({
                'position': 'absolute',
                'background-color': '#06628d',
                'bottom': '0',
                'height': '1px'
            }).animate({
                'left': liPos,
                'width': lineWidth
            }, 200);
        }

        lineSet();

        nav.find('li').on('click', function () {

            activeLi.removeClass('active__item');

            $(this).addClass('active__item');

            refresh();

            lineSet();
        });
    };

